var fs = require('fs')
  , buffer = require('buffer')
  , Routes = require('./routes')
  , _ = require('underscore')
  , Q = require('q')
  , stream = require('stream')
  , jade = require('jade')
  ;

function JadeDebugger (opts) {
  var self = this;
  self.opts = opts || {};
  self.Routes = Routes(self.opts);

  return this;
};

JadeDebugger.prototype.checkFile = function checkFile (filename, file) {
  var self = this
    , deferred = Q.defer()
    ;


  deferred.resolve(file);

  return deferred.promise;
};

module.exports = function (opts) {
  return new JadeDebugger(opts);
};