var Q = require('q');

module.exports = function (opts) {
  var app = opts.libs.app
    , libs = opts.libs
    ;

  app.post('/api/codedebugger/jade/:file', function (req, res) {
    libs.jadedebugger.checkFile( opts.baseDir + '/views/' + req.params.file, req.params.type, req.body ).then(function(results) {
      res.send(results);
    });
  });

};