var Q = require('q');

module.exports = function (opts) {
  var app = opts.libs.app
    , libs = opts.libs
    ;


  /**
   * Get all files/folders in a directory
   */
  app.get('/api/github/search/:keyword', function (req, res) {
    libs.github.search(req.params.keyword).then(function(results) {
      res.send(results);
    });
  });

};