var Q = require('q');

module.exports = function (opts) {
  var app = opts.libs.app
    , libs = opts.libs
    ;


  /**
   * Get all files/folders in a directory
   */
  app.get('/api/mongo/collections/all', function (req, res) {
    libs.mongo.all().then(function(results) {
      res.send(results);
    });
  });

};